import React, { useState, createContext, useContext, useCallback } from 'react';
import LoginPage from './LoginPage';

export const AppContext = createContext(null);

const SectionTitle = ({ children }) => (
    <h3 className="text-xl font-semibold text-gray-800 mb-4">{children}</h3>
);

const FeatureCard = ({ title, onClick }) => (
    <button
        onClick={onClick}
        className="flex flex-col items-center justify-center p-6 bg-white rounded-lg shadow-md hover:shadow-lg transition duration-300 transform hover:-translate-y-1 cursor-pointer"
    >
        <span className="text-4xl mb-2">{title[0]}</span>
        <p className="text-lg font-medium text-gray-700">{title}</p>
    </button>
);

const DashboardLayout = ({ children, title }) => {
    const { currentUser, setCurrentPage, setCurrentUser, currentPage } = useContext(AppContext);

    const getNavItems = (role) => {
        switch (role) {
            case 'student':
                return [
                    { name: 'Attendance', page: 'attendance' },
                    { name: 'Grades', page: 'grades' },
                    { name: 'Leaves', page: 'leaves' },
                    { name: 'Library', page: 'library' },
                    { name: 'Payments', page: 'payments' },
                    { name: 'Timetable', page: 'timetable' },
                    { name: 'My Info Corner', page: 'myinfo' },
                ];
            case 'parent':
                return [
                    { name: 'Attendance', page: 'attendance' },
                    { name: 'Grades', page: 'grades' },
                    { name: 'Leaves', page: 'leaves' },
                    { name: 'Library', page: 'library' },
                    { name: 'Payments', page: 'payments' },
                    { name: 'Timetable', page: 'timetable' },
                    { name: 'My Info Corner', page: 'myinfo' },
                ];
            case 'teacher':
                return [
                    { name: 'Attendance', page: 'attendance' },
                    { name: 'Grades', page: 'grades' },
                    { name: 'Leaves', page: 'leaves' },
                    { name: 'Library', page: 'library' },
                    { name: 'Timetable', page: 'timetable' },
                    { name: 'My Info Corner', page: 'myinfo' },
                ];
            case 'admin':
            case 'principal':
                return [
                    { name: 'Attendance', page: 'attendance' },
                    { name: 'College In/Out Log', page: 'college-logs' },
                    { name: 'Grades', page: 'grades' },
                    { name: 'Hostel Attendance', page: 'hostel-attendance' },
                    { name: 'Leaves', page: 'leaves' },
                    { name: 'Library', page: 'library' },
                    { name: 'Payments', page: 'payments' },
                    { name: 'Timetable', page: 'timetable' },
                    { name: 'Students & Faculty Info', page: 'users-info' },
                    { name: 'My Info Corner', page: 'myinfo' },
                ];
            default:
                return [];
        }
    };

    const navItems = getNavItems(currentUser?.role);

    const handleLogout = () => {
        setCurrentUser(null);
        setCurrentPage('login');
    };

    return (
        <div className="min-h-screen bg-gray-50 flex flex-col">
            <header className="bg-blue-700 text-white p-4 shadow-md flex justify-between items-center">
                <h1 className="text-2xl font-bold">MAMC Portal - {title}</h1>
                <div className="flex items-center space-x-4">
                    <span className="text-lg">Welcome, {currentUser?.username || 'User'} ({currentUser?.role})</span>
                    <button
                        onClick={handleLogout}
                        className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-lg shadow-sm transition duration-200"
                    >
                        Logout
                    </button>
                </div>
            </header>

            <div className="flex flex-grow">
                <nav className="w-64 bg-white shadow-lg p-6 flex flex-col space-y-4">
                    {navItems.map((item) => (
                        <button
                            key={item.page}
                            onClick={() => setCurrentPage(item.page)}
                            className="w-full text-left py-3 px-4 rounded-lg text-gray-700 hover:bg-blue-100 hover:text-blue-700 transition duration-200 text-lg"
                        >
                            {item.name}
                        </button>
                    ))}
                </nav>

                <main className="flex-grow p-8 bg-gray-50 overflow-auto">
                    {currentPage !== 'dashboard' && currentPage !== 'login' && (
                        <button
                            onClick={() => setCurrentPage('dashboard')}
                            className="mb-6 bg-gray-300 hover:bg-gray-400 text-gray-800 py-2 px-4 rounded-lg shadow-sm transition duration-200 flex items-center"
                        >
                            <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
                            </svg>
                            Back to Dashboard
                        </button>
                    )}
                    {children}
                </main>
            </div>
        </div>
    );
};

const StudentDashboard = () => {
    const { setCurrentPage } = useContext(AppContext);
    return (
        <DashboardLayout title="Student Dashboard">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Student Home</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <FeatureCard title="Attendance" onClick={() => setCurrentPage('attendance')} />
                <FeatureCard title="Grades" onClick={() => setCurrentPage('grades')} />
                <FeatureCard title="Leaves" onClick={() => setCurrentPage('leaves')} />
                <FeatureCard title="Library" onClick={() => setCurrentPage('library')} />
                <FeatureCard title="Payments" onClick={() => setCurrentPage('payments')} />
                <FeatureCard title="Timetable" onClick={() => setCurrentPage('timetable')} />
                <FeatureCard title="My Info Corner" onClick={() => setCurrentPage('myinfo')} />
            </div>
        </DashboardLayout>
    );
};

const ParentDashboard = () => {
    const { setCurrentPage } = useContext(AppContext);
    return (
        <DashboardLayout title="Parent Dashboard">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Parent Home</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <FeatureCard title="Attendance" onClick={() => setCurrentPage('attendance')} />
                <FeatureCard title="Grades" onClick={() => setCurrentPage('grades')} />
                <FeatureCard title="Leaves" onClick={() => setCurrentPage('leaves')} />
                <FeatureCard title="Library" onClick={() => setCurrentPage('library')} />
                <FeatureCard title="Payments" onClick={() => setCurrentPage('payments')} />
                <FeatureCard title="Timetable" onClick={() => setCurrentPage('timetable')} />
                <FeatureCard title="My Info Corner" onClick={() => setCurrentPage('myinfo')} />
            </div>
        </DashboardLayout>
    );
};

const TeacherDashboard = () => {
    const { setCurrentPage } = useContext(AppContext);
    return (
        <DashboardLayout title="Teacher Dashboard">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Teacher Home</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <FeatureCard title="Attendance" onClick={() => setCurrentPage('attendance')} />
                <FeatureCard title="Grades" onClick={() => setCurrentPage('grades')} />
                <FeatureCard title="Leaves" onClick={() => setCurrentPage('leaves')} />
                <FeatureCard title="Library" onClick={() => setCurrentPage('library')} />
                <FeatureCard title="Timetable" onClick={() => setCurrentPage('timetable')} />
                <FeatureCard title="My Info Corner" onClick={() => setCurrentPage('myinfo')} />
            </div>
        </DashboardLayout>
    );
};

const AdminDashboard = () => {
    const { setCurrentPage } = useContext(AppContext);
    return (
        <DashboardLayout title="Admin/Principal Dashboard">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Admin/Principal Home</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <FeatureCard title="Attendance" onClick={() => setCurrentPage('attendance')} />
                <FeatureCard title="College In/Out Log" onClick={() => setCurrentPage('college-logs')} />
                <FeatureCard title="Grades" onClick={() => setCurrentPage('grades')} />
                <FeatureCard title="Hostel Attendance" onClick={() => setCurrentPage('hostel-attendance')} />
                <FeatureCard title="Leaves" onClick={() => setCurrentPage('leaves')} />
                <FeatureCard title="Library" onClick={() => setCurrentPage('library')} />
                <FeatureCard title="Payments" onClick={() => setCurrentPage('payments')} />
                <FeatureCard title="Timetable" onClick={() => setCurrentPage('timetable')} />
                <FeatureCard title="Students & Faculty Info" onClick={() => setCurrentPage('users-info')} />
                <FeatureCard title="My Info Corner" onClick={() => setCurrentPage('myinfo')} />
            </div>
        </DashboardLayout>
    );
};

const MyInfoCornerPage = () => {
    const { currentUser } = useContext(AppContext);
    const [userInfo, setUserInfo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    React.useEffect(() => {
        const fetchUserInfo = async () => {
            setLoading(true);
            setError('');
            if (!currentUser || !currentUser.token) {
                setError('User not authenticated for My Info Corner.');
                setLoading(false);
                console.log('MyInfoCornerPage: currentUser or token missing.', currentUser); // Debug log
                return;
            }
            console.log('MyInfoCornerPage: Fetching user info with token:', currentUser.token); // Debug log

            try {
                const response = await fetch('http://127.0.0.1:8000/api/users/me/', {
                    headers: { 'Authorization': `Bearer ${currentUser.token}` }
                });
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || 'Failed to fetch user info');
                }
                const data = await response.json();
                setUserInfo(data);
                setLoading(false);
                console.log('MyInfoCornerPage: User info fetched successfully.', data); // Debug log
            } catch (err) {
                setError(`Failed to load user information: ${err.message}`);
                setLoading(false);
                console.error('Fetch error (MyInfoCorner):', err);
            }
        };
        fetchUserInfo(); // Call fetchUserInfo directly inside useEffect
    }, [currentUser]);

    if (loading) return <div className="text-center py-8">Loading your information...</div>;
    if (error) return <div className="text-center py-8 text-red-600">{error}</div>;

    return (
        <div>
            <SectionTitle>My Info Corner</SectionTitle>
            {userInfo ? (
                <div className="bg-white p-6 rounded-lg shadow-md max-w-lg mx-auto">
                    <p className="mb-2"><strong className="text-gray-700">Username:</strong> {userInfo.username}</p>
                    <p className="mb-2"><strong className="text-gray-700">Role:</strong> {userInfo.role.charAt(0).toUpperCase() + userInfo.role.slice(1)}</p>
                    <p className="mb-2"><strong className="text-gray-700">Full Name:</strong> {userInfo.full_name || 'N/A'}</p>
                    <p className="mb-2"><strong className="text-gray-700">Email:</strong> {userInfo.email || 'N/A'}</p>
                    {userInfo.student_id && <p className="mb-2"><strong className="text-gray-700">Student ID:</strong> {userInfo.student_id}</p>}
                    {userInfo.batch && <p className="mb-2"><strong className="text-gray-700">Batch:</strong> {userInfo.batch}</p>}
                    {userInfo.faculty_id && <p className="mb-2"><strong className="text-gray-700">Faculty ID:</strong> {userInfo.faculty_id}</p>}
                    {userInfo.department && <p className="mb-2"><strong className="text-gray-700">Department:</strong> {userInfo.department}</p>}
                    {userInfo.child_name && <p className="mb-2"><strong className="text-gray-700">Child's Name:</strong> {userInfo.child_name}</p>}
                    {userInfo.admin_level && <p className="mb-2"><strong className="text-gray-700">Admin Level:</strong> {userInfo.admin_level}</p>}
                    <p><strong className="text-gray-700">Date Joined:</strong> {userInfo.date_joined || 'N/A'}</p>
                </div>
            ) : (
                <p className="text-center text-gray-600">No user information available.</p>
            )}
        </div>
    );
};

const AttendancePage = () => {
    const { currentUser } = useContext(AppContext);
    const [attendanceData, setAttendanceData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedStudentId, setSelectedStudentId] = useState('');
    const [batches, setBatches] = useState([]);
    const [selectedBatchId, setSelectedBatchId] = useState('');

    React.useEffect(() => {
        const fetchAttendance = async () => {
            setLoading(true);
            setError('');
            let url = '';

            if (!currentUser || !currentUser.token) {
                setError('User not authenticated for Attendance.');
                setLoading(false);
                console.log('AttendancePage: currentUser or token missing.', currentUser); // Debug log
                return;
            }

            try {
                if (currentUser.role === 'student') {
                    url = `http://127.0.0.1:8000/api/attendance/my/`;
                } else if (currentUser.role === 'parent') {
                    url = `http://127.0.0.1:8000/api/parent/student/attendance/`;
                } else if (currentUser.role === 'teacher') {
                    if (batches.length === 0) {
                        try {
                            const batchResponse = await fetch('http://127.0.0.1:8000/api/timetable/batches/', {
                                headers: { 'Authorization': `Bearer ${currentUser.token}` }
                            });
                            if (!batchResponse.ok) {
                                const errorData = await batchResponse.json();
                                throw new Error(errorData.message || 'Failed to fetch batches');
                            }
                            const batchData = await batchResponse.json();
                            setBatches(batchData);
                            if (batchData.length > 0) setSelectedBatchId(batchData[0].id);
                        } catch (err) {
                            setError(`Failed to load batches: ${err.message}`);
                            setLoading(false);
                            console.error('Fetch error (Batches):', err);
                            return;
                        }
                    }
                    if (!selectedBatchId) {
                        setAttendanceData([]);
                        setLoading(false);
                        return;
                    }
                    url = `http://127.0.0.1:8000/api/attendance/batch/${selectedBatchId}/`;
                } else if (currentUser.role === 'admin' || currentUser.role === 'principal') {
                    if (batches.length === 0) {
                        try {
                            const batchResponse = await fetch('http://127.0.0.1:8000/api/timetable/batches/', {
                                headers: { 'Authorization': `Bearer ${currentUser.token}` }
                            });
                            if (!batchResponse.ok) {
                                const errorData = await batchResponse.json();
                                throw new Error(errorData.message || 'Failed to fetch batches');
                            }
                            const batchData = await batchResponse.json();
                            setBatches(batchData);
                        } catch (err) {
                            setError(`Failed to load batches: ${err.message}`);
                            setLoading(false);
                            console.error('Fetch error (Batches):', err);
                            return;
                        }
                    }

                    if (selectedStudentId) {
                        url = `http://127.0.0.1:8000/api/attendance/student/${selectedStudentId}/`;
                    } else if (selectedBatchId) {
                        url = `http://127.0.0.1:8000/api/attendance/batch/${selectedBatchId}/`;
                    } else {
                        url = `http://127.0.0.1:8000/api/attendance/all/`;
                    }
                } else {
                    setError('Unauthorized access for attendance.');
                    setLoading(false);
                    return;
                }

                if (!url) {
                    setLoading(false);
                    return;
                }

                console.log('AttendancePage: Fetching attendance from URL:', url); // Debug log
                const response = await fetch(url, {
                    headers: { 'Authorization': `Bearer ${currentUser.token}` }
                });
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || 'Failed to fetch attendance data');
                }
                const data = await response.json();
                setAttendanceData(data);
                setLoading(false);
                console.log('AttendancePage: Attendance data fetched successfully.', data); // Debug log

            } catch (err) {
                setError(`Failed to load attendance data: ${err.message}`);
                setLoading(false);
                console.error('Fetch error (Attendance):', err);
            }
        };
        fetchAttendance();
    }, [currentUser, selectedBatchId, selectedStudentId, batches.length]);

    const renderAttendanceTable = () => {
        if (loading) return <div className="text-center py-4">Loading attendance...</div>;
        if (error) return <div className="text-center py-4 text-red-600">{error}</div>;
        if (attendanceData.length === 0) return <div className="text-center py-4 text-gray-600">No attendance records found.</div>;

        switch (currentUser.role) {
            case 'student':
            case 'parent':
                return (
                    <div className="overflow-x-auto">
                        <table className="min-w-full bg-white border border-gray-200 rounded-lg shadow-sm">
                            <thead>
                                <tr className="bg-gray-100 border-b">
                                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Subject</th>
                                    <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {attendanceData.map((record) => (
                                    <tr key={record.id || `${record.date}-${record.subject}`} className="border-b last:border-b-0 hover:bg-gray-50">
                                        <td className="px-4 py-3 whitespace-nowrap">{record.date}</td>
                                        <td className="px-4 py-3 whitespace-nowrap">{record.subject}</td>
                                        <td className={`px-4 py-3 whitespace-nowrap font-medium ${
                                            record.status === 'Present' ? 'text-green-600' :
                                            record.status === 'Absent' ? 'text-red-600' : 'text-gray-500'
                                        }`}>{record.status}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                );
            case 'teacher':
                return (
                    <>
                        <div className="mb-4">
                            <label htmlFor="batchSelect" className="block text-gray-700 text-sm font-bold mb-2">Select Batch:</label>
                            <select
                                id="batchSelect"
                                value={selectedBatchId}
                                onChange={(e) => setSelectedBatchId(e.target.value)}
                                className="shadow border rounded w-1/2 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            >
                                <option value="">Select a Batch</option>
                                {batches.map(batch => (
                                    <option key={batch.id} value={batch.id}>{batch.name}</option>
                                ))}
                            </select>
                        </div>
                        <div className="overflow-x-auto">
                            <table className="min-w-full bg-white border border-gray-200 rounded-lg shadow-sm">
                                <thead>
                                    <tr className="bg-gray-100 border-b">
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Name</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Subject</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Batch</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {attendanceData.map((record) => (
                                        <tr key={record.id || `${record.date}-${record.studentId}`} className="border-b last:border-b-0 hover:bg-gray-50">
                                            <td className="px-4 py-3 whitespace-nowrap">{record.studentName}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">{record.subject}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">{record.date}</td>
                                            <td className={`px-4 py-3 whitespace-nowrap font-medium ${
                                                record.status === 'Present' ? 'text-green-600' :
                                                record.status === 'Absent' ? 'text-red-600' : 'text-gray-500'
                                            }`}>{record.status}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">{record.batch}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">
                                                <button className="text-blue-600 hover:text-blue-800 mr-2">Edit</button>
                                                <button className="text-red-600 hover:text-red-800">Delete</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                        <button className="mt-4 bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg shadow-sm">Mark Attendance</button>
                    </>
                );
            case 'admin':
            case 'principal':
                return (
                    <>
                        <div className="mb-4 flex space-x-4">
                            <div>
                                <label htmlFor="batchSelect" className="block text-gray-700 text-sm font-bold mb-2">Filter by Batch:</label>
                                <select
                                    id="batchSelect"
                                    value={selectedBatchId}
                                    onChange={(e) => { setSelectedBatchId(e.target.value); setSelectedStudentId(''); }}
                                    className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                >
                                    <option value="">All Batches</option>
                                    {batches.map(batch => (
                                        <option key={batch.id} value={batch.id}>{batch.name}</option>
                                    ))}
                                </select>
                            </div>
                            <div>
                                <label htmlFor="studentIdInput" className="block text-gray-700 text-sm font-bold mb-2">View Single Student (ID):</label>
                                <input
                                    type="text"
                                    id="studentIdInput"
                                    placeholder="Enter Student ID"
                                    value={selectedStudentId}
                                    onChange={(e) => { setSelectedStudentId(e.target.value); setSelectedBatchId(''); }}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                />
                            </div>
                        </div>
                        <div className="overflow-x-auto">
                            <table className="min-w-full bg-white border border-gray-200 rounded-lg shadow-sm">
                                <thead>
                                    <tr className="bg-gray-100 border-b">
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student ID</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Name</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Batch</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Attendance %</th>
                                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Details</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {attendanceData.map((record) => (
                                        <tr key={record.id || `${record.date}-${record.studentId}`} className="border-b last:border-b-0 hover:bg-gray-50">
                                            <td className="px-4 py-3 whitespace-nowrap">{record.studentId}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">{record.studentName}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">{record.batch}</td>
                                            <td className="px-4 py-3 whitespace-nowrap">{record.percentage}%</td>
                                            <td className="px-4 py-3 whitespace-nowrap">
                                                <button onClick={() => setSelectedStudentId(record.studentId)} className="text-blue-600 hover:text-blue-800">View</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </>
                );
            default:
                return <div className="text-center py-4 text-gray-600">No attendance view for this role.</div>;
        }
    };

    return (
        <div>
            <SectionTitle>Attendance</SectionTitle>
            {renderAttendanceTable()}
        </div>
    );
};

const GradesPage = () => {
    const { currentUser } = useContext(AppContext);
    const [gradesData, setGradesData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedStudentId, setSelectedStudentId] = useState('');

    React.useEffect(() => {
        const fetchGrades = async () => {
            setLoading(true);
            setError('');
            let url = '';

            if (!currentUser || !currentUser.token) {
                setError('User not authenticated for Grades.');
                setLoading(false);
                console.log('GradesPage: currentUser or token missing.', currentUser); // Debug log
                return;
            }

            try {
                if (currentUser.role === 'student') {
                    url = `http://127.0.0.1:8000/api/grades/my/`;
                } else if (currentUser.role === 'parent') {
                    url = `http://127.0.0.1:8000/api/parent/student/grades/`;
                } else if (currentUser.role === 'teacher' || currentUser.role === 'admin' || currentUser.role === 'principal') {
                    if (selectedStudentId) {
                        url = `http://127.0.0.1:8000/api/grades/student/${selectedStudentId}/`;
                    } else {
                        url = `http://127.0.0.1:8000/api/grades/all/`;
                    }
                } else {
                    setError('Unauthorized access for grades.');
                    setLoading(false);
                    return;
                }

                console.log('GradesPage: Fetching grades from URL:', url); // Debug log
                const response = await fetch(url, {
                    headers: { 'Authorization': `Bearer ${currentUser.token}` }
                });
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || 'Failed to fetch grades data');
                }
                const data = await response.json();
                setGradesData(data);
                setLoading(false);
                console.log('GradesPage: Grades data fetched successfully.', data); // Debug log

            } catch (err) {
                setError(`Failed to load grades data: ${err.message}`);
                setLoading(false);
                console.error('Fetch error (Grades):', err);
            }
        };
        fetchGrades();
    }, [currentUser, selectedStudentId]);

    const renderGradesTable = () => {
        if (loading) return <div className="text-center py-4">Loading grades...</div>;
        if (error) return <div className="text-center py-4 text-red-600">{error}</div>;
        if (gradesData.length === 0) return <div className="text-center py-4 text-gray-600">No grades records found.</div>;

        return (
            <div className="overflow-x-auto">
                {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && (
                    <div className="mb-4">
                        <label htmlFor="studentIdInput" className="block text-gray-700 text-sm font-bold mb-2">Filter by Student ID:</label>
                        <input
                            type="text"
                            id="studentIdInput"
                            placeholder="Enter Student ID"
                            value={selectedStudentId}
                            onChange={(e) => setSelectedStudentId(e.target.value)}
                            className="shadow appearance-none border rounded w-1/2 py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        />
                    </div>
                )}
                <table className="min-w-full bg-white border border-gray-200 rounded-lg shadow-sm">
                    <thead>
                        <tr className="bg-gray-100 border-b">
                            {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Name</th>}
                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Subject</th>
                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Exam</th>
                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Marks</th>
                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Max Marks</th>
                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Percentage</th>
                            {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && (
                                <th scope="col" className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                            )}
                        </tr>
                    </thead>
                    <tbody>
                        {gradesData.map((grade) => (
                            <tr key={grade.id || `${grade.subject}-${grade.exam}`} className="border-b last:border-b-0 hover:bg-gray-50">
                                {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && <td className="px-4 py-3 whitespace-nowrap">{grade.studentName || 'N/A'}</td>}
                                <td className="px-4 py-3 whitespace-nowrap">{grade.subject}</td>
                                <td className="px-4 py-3 whitespace-nowrap">{grade.exam}</td>
                                <td className="px-4 py-3 whitespace-nowrap">{grade.marks}</td>
                                <td className="px-4 py-3 whitespace-nowrap">{grade.maxMarks}</td>
                                <td className="px-4 py-3 whitespace-nowrap">{((grade.marks / grade.maxMarks) * 100).toFixed(2)}%</td>
                                {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && (
                                    <td className="px-4 py-3 whitespace-nowrap">
                                        <button className="text-blue-600 hover:text-blue-800 mr-2">Edit</button>
                                        <button className="text-red-600 hover:text-red-800">Delete</button>
                                    </td>
                                )}
                            </tr>
                        ))}
                    </tbody>
                </table>
                {(currentUser.role === 'teacher' || currentUser.role === 'admin') && (
                    <button className="mt-4 bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-lg shadow-sm">Add Grade</button>
                )}
            </div>
        );
    };

    return (
        <div>
            <SectionTitle>Grades & Marks</SectionTitle>
            {renderGradesTable()}
        </div>
    );
};

const LeavesPage = () => {
    const { currentUser } = useContext(AppContext);
    const [leavesData, setLeavesData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [leaveForm, setLeaveForm] = useState({ startDate: '', endDate: '', reason: '' });
    const [formMessage, setFormMessage] = useState({ text: '', type: '' });

    const handleLeaveSubmit = async (e) => {
        e.preventDefault();
        setFormMessage({ text: '', type: '' });
        if (!leaveForm.startDate || !leaveForm.endDate || !leaveForm.reason) {
            setFormMessage({ text: 'Please fill all fields.', type: 'error' });
            return;
        }

        if (!currentUser || !currentUser.token) {
            setFormMessage({ text: 'User not authenticated to submit leave.', type: 'error' });
            return;
        }
        console.log('LeavesPage: Submitting leave with token:', currentUser.token); // Debug log

        try {
            const response = await fetch('http://127.0.0.1:8000/api/leaves/apply/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${currentUser.token}`
                },
                body: JSON.stringify(leaveForm)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to submit leave request');
            }
            await response.json();
            setFormMessage({ text: 'Leave request submitted successfully!', type: 'success' });
            fetchLeaves();
            setLeaveForm({ startDate: '', endDate: '', reason: '' });
            console.log('LeavesPage: Leave submitted successfully.'); // Debug log
        } catch (err) {
            setFormMessage({ text: `Failed to submit leave: ${err.message}`, type: 'error' });
            console.error('Fetch error (Submit Leave):', err);
        }
    };

    const fetchLeaves = useCallback(async () => {
        setLoading(true);
        setError('');
        let url = '';
        if (!currentUser || !currentUser.token) {
            setError('User not authenticated for Leaves.');
            setLoading(false);
            console.log('LeavesPage: currentUser or token missing for fetch.', currentUser); // Debug log
            return;
        }

        try {
            if (currentUser.role === 'student') {
                url = `http://127.0.0.1:8000/api/leaves/my/`;
            } else if (currentUser.role === 'teacher' || currentUser.role === 'admin' || currentUser.role === 'principal') {
                url = `http://127.0.0.1:8000/api/leaves/all/`;
            } else {
                setError('Unauthorized access for leaves.');
                setLoading(false);
                return;
            }

            console.log('LeavesPage: Fetching leaves from URL:', url); // Debug log
            const response = await fetch(url, {
                headers: { 'Authorization': `Bearer ${currentUser.token}` }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to load leaves data');
            }
            setLeavesData(await response.json());
            setLoading(false);
            console.log('LeavesPage: Leaves data fetched successfully.'); // Debug log
        } catch (err) {
            setError(`Failed to load leaves data: ${err.message}`);
            setLoading(false);
            console.error('Fetch error (Fetch Leaves):', err);
        }
    }, [currentUser]);

    React.useEffect(() => {
        fetchLeaves();
    }, [fetchLeaves]);

    const renderLeavesContent = () => {
        if (loading) return <div className="text-center py-4">Loading leaves...</div>;
        if (error) return <div className="text-center py-4 text-red-600">{error}</div>;

        return (
            <>
                {currentUser.role === 'student' && (
                    <div className="bg-white p-6 rounded-lg shadow-md mb-6">
                        <SectionTitle>Apply for Leave</SectionTitle>
                        <form onSubmit={handleLeaveSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label htmlFor="startDate" className="block text-gray-700 text-sm font-bold mb-2">Start Date:</label>
                                <input
                                    type="date"
                                    id="startDate"
                                    value={leaveForm.startDate}
                                    onChange={(e) => setLeaveForm({ ...leaveForm, startDate: e.target.value })}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    required
                                />
                            </div>
                            <div>
                                <label htmlFor="endDate" className="block text-gray-700 text-sm font-bold mb-2">End Date:</label>
                                <input
                                    type="date"
                                    id="endDate"
                                    value={leaveForm.endDate}
                                    onChange={(e) => setLeaveForm({ ...leaveForm, endDate: e.target.value })}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    required
                                />
                            </div>
                            <div className="col-span-full">
                                <label htmlFor="reason" className="block text-gray-700 text-sm font-bold mb-2">Reason:</label>
                                <textarea
                                    id="reason"
                                    rows="3"
                                    value={leaveForm.reason}
                                    onChange={(e) => setLeaveForm({ ...leaveForm, reason: e.target.value })}
                                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                    placeholder="Briefly describe your reason for leave"
                                    required
                                ></textarea>
                            </div>
                            <div className="col-span-full">
                                <button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg shadow-md transition duration-200">
                                    Submit Leave Request
                                </button>
                                {formMessage.text && (
                                    <p className={`mt-2 text-sm ${formMessage.type === 'success' ? 'text-green-600' : 'text-red-600'}`}>
                                        {formMessage.text}
                                    </p>
                                )}
                            </div>
                        </form>
                    </div>
                )}

                <SectionTitle>{currentUser.role === 'student' ? 'My Leave Requests' : 'All Leave Requests'}</SectionTitle>
                {leavesData.length === 0 ? (
                    <div className="text-center py-4 text-gray-600">No leave requests found.</div>
                ) : (
                    <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-gray-50">
                                <tr>
                                    {(currentUser.role !== 'student' && currentUser.role !== 'parent') && <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Applicant</th>}
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Start Date</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">End Date</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Reason</th>
                                    <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                                    {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && (
                                        <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                                    )}
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {leavesData.map((leave) => (
                                    <tr key={leave.id || `${leave.startDate}-${leave.user}`} className="border-b last:border-b-0 hover:bg-gray-50">
                                        {(currentUser.role !== 'student' && currentUser.role !== 'parent') && <td className="px-6 py-4 whitespace-nowrap">{leave.user || 'N/A'} ({leave.role || 'N/A'})</td>}
                                        <td className="px-6 py-4 whitespace-nowrap">{leave.startDate}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{leave.endDate}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{leave.reason}</td>
                                        <td className={`px-6 py-4 whitespace-nowrap font-semibold ${
                                            leave.status === 'Approved' ? 'text-green-600' :
                                            leave.status === 'Rejected' ? 'text-red-600' : 'text-yellow-600'
                                        }`}>{leave.status}</td>
                                        {(currentUser.role === 'admin' || currentUser.role === 'principal' || currentUser.role === 'teacher') && (
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                {leave.status === 'Pending' && (
                                                    <>
                                                        <button className="text-green-600 hover:text-green-900 mr-2">Approve</button>
                                                        <button className="text-red-600 hover:text-red-900">Reject</button>
                                                    </>
                                                )}
                                                <button className="text-indigo-600 hover:text-indigo-900 ml-2">View</button>
                                            </td>
                                        )}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </>
        );
    };

    return (
        <div>
            <SectionTitle>Leaves</SectionTitle>
            {renderLeavesContent()}
        </div>
    );
};

const LibraryPage = () => {
    const { currentUser } = useContext(AppContext);
    const [books, setBooks] = useState([]);
    const [borrowedBooks, setBorrowedBooks] = useState([]);
    const [libraryLogs, setLibraryLogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    React.useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            setError('');
            if (!currentUser || !currentUser.token) {
                setError('User not authenticated for Library.');
                setLoading(false);
                console.log('LibraryPage: currentUser or token missing.', currentUser); // Debug log
                return;
            }
            console.log('LibraryPage: Fetching library data with token:', currentUser.token); // Debug log

            try {
                const availableBooksResponse = await fetch('http://127.0.0.1:8000/api/library/available-books/', {
                    headers: { 'Authorization': `Bearer ${currentUser.token}` }
                });
                if (!availableBooksResponse.ok) {
                    const errorData = await availableBooksResponse.json();
                    throw new Error(errorData.message || 'Failed to fetch available books');
                }
                const availableBooksData = await availableBooksResponse.json();
                setBooks(availableBooksData);
                console.log('LibraryPage: Available books fetched successfully.', availableBooksData); // Debug log

                if (currentUser.role === 'student') {
                    const borrowedResponse = await fetch('http://127.0.0.1:8000/api/library/borrowed/', {
                        headers: { 'Authorization': `Bearer ${currentUser.token}` }
                    });
                    if (!borrowedResponse.ok) {
                        const errorData = await borrowedResponse.json();
                        throw new Error(errorData.message || 'Failed to fetch borrowed books');
                    }
                    const borrowedData = await borrowedResponse.json();
                    setBorrowedBooks(borrowedData);
                    console.log('LibraryPage: Borrowed books fetched successfully.', borrowedData); // Debug log

                    const logsResponse = await fetch('http://127.0.0.1:8000/api/library/attendance/', {
                        headers: { 'Authorization': `Bearer ${currentUser.token}` }
                    });
                    if (!logsResponse.ok) {
                        const errorData = await logsResponse.json();
                        throw new Error(errorData.message || 'Failed to fetch library attendance logs');
                    }
                    const logsData = await logsResponse.json();
                    setLibraryLogs(logsData);
                    console.log('LibraryPage: Library logs fetched successfully.', logsData); // Debug log
                }

                setLoading(false);
            } catch (err) {
                setError(`Failed to load library data: ${err.message}`);
                setLoading(false);
                console.error('Fetch error (Library):', err);
            }
        };
        fetchData();
    }, [currentUser]);

    if (loading) return <div className="text-center py-8">Loading library data...</div>;
    if (error) return <div className="text-center py-8 text-red-600">{error}</div>;

    return (
        <div>
            <SectionTitle>Library</SectionTitle>

            <div className="mb-8">
                <h4 className="text-lg font-semibold text-gray-700 mb-3">Available Books</h4>
                {books.length === 0 ? (
                    <p className="text-gray-600">No books available.</p>
                ) : (
                    <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Title</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Author</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Genre</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Available Copies</th>
                                    {currentUser.role === 'student' && (
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                                    )}
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {books.map((book) => (
                                    <tr key={book.id}>
                                        <td className="px-6 py-4 whitespace-nowrap">{book.title}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{book.author}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{book.genre}</td>
                                        <td className="px-6 py-4 whitespace-nowrap">{book.available}</td>
                                        {currentUser.role === 'student' && (
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                {book.available > 0 ? (
                                                    <button className="text-blue-600 hover:text-blue-900">Borrow</button>
                                                ) : (
                                                    <span className="text-gray-500">Out of Stock</span>
                                                )}
                                            </td>
                                        )}
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>

            {currentUser.role === 'student' && (
                <>
                    <div className="mb-8">
                        <h4 className="text-lg font-semibold text-gray-700 mb-3">My Borrowed Books</h4>
                        {borrowedBooks.length === 0 ? (
                            <p className="text-gray-600">You have not borrowed any books.</p>
                        ) : (
                            <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                                <table className="min-w-full divide-y divide-gray-200">
                                    <thead className="bg-gray-50">
                                        <tr>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Book Title</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Borrow Date</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Return Date</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody className="bg-white divide-y divide-gray-200">
                                        {borrowedBooks.map((borrow) => (
