const PaymentsPage = () => {
    const { currentUser } = useContext(AppContext);
    const [paymentsData, setPaymentsData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedFile, setSelectedFile] = useState(null);
    const [uploadMessage, setUploadMessage] = useState({ text: '', type: '' });

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleUploadSubmit = async (event) => {
        event.preventDefault();
        setUploadMessage({ text: '', type: '' });
        if (!selectedFile) {
            setUploadMessage({ text: 'Please select a file to upload.', type: 'error' });
            return;
        }

        if (!currentUser || !currentUser.token) {
            setUploadMessage({ text: 'User not authenticated to submit payment proof.', type: 'error' });
            return;
        }
        console.log('PaymentsPage: Submitting payment proof with token:', currentUser.token); // Debug log

        const formData = new FormData();
        formData.append('proof', selectedFile);

        setUploadMessage({ text: 'Uploading proof...', type: 'info' });
        try {
            const response = await fetch('http://127.0.0.1:8000/api/payments/submit-proof/', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${currentUser.token}`
                },
                body: formData,
            });
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to upload proof');
            }
            await response.json();
            setUploadMessage({ text: 'Payment proof uploaded successfully!', type: 'success' });
            setSelectedFile(null);
            fetchPayments();
            console.log('PaymentsPage: Payment proof submitted successfully.'); // Debug log
        } catch (err) {
            setUploadMessage({ text: `Failed to upload payment proof: ${err.message}`, type: 'error' });
            console.error('Fetch error (Upload Proof):', err);
        }
    };

    const fetchPayments = useCallback(async () => {
        setLoading(true);
        setError('');
        let url = '';
        if (!currentUser || !currentUser.token) {
            setError('User not authenticated for Payments.');
            setLoading(false);
            console.log('PaymentsPage: currentUser or token missing for fetch.', currentUser); // Debug log
            return;
        }

        try {
            if (currentUser.role === 'student' || currentUser.role === 'parent') {
                url = `http://127.0.0.1:8000/api/payments/my/`;
            } else if (currentUser.role === 'admin' || currentUser.role === 'principal') {
                url = `http://127.0.0.1:8000/api/payments/all/`;
            } else {
                setError('Unauthorized access for payments.');
                setLoading(false);
                return;
            }

            console.log('PaymentsPage: Fetching payments from URL:', url); // Debug log
            const response = await fetch(url, {
                headers: { 'Authorization': `Bearer ${currentUser.token}` }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to load payments data');
            }
            setPaymentsData(await response.json());
            setLoading(false);
            console.log('PaymentsPage: Payments data fetched successfully.'); // Debug log
        } catch (err) {
            setError(`Failed to load payments data: ${err.message}`);
            setLoading(false);
            console.error('Fetch error (Fetch Payments):', err);
        }
    }, [currentUser]);

    React.useEffect(() => {
        fetchPayments();
    }, [fetchPayments]);

    const renderPaymentsTable = () => {
        if (loading) return <div className="text-center py-4">Loading payments...</div>;
        if (error) return <div className="text-center py-4 text-red-600">{error}</div>;
        if (paymentsData.length === 0) return <div className="text-center py-4 text-gray-600">No payment records found.</div>;

        return (
            <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                        <tr>
                            {(currentUser.role === 'admin' || currentUser.role === 'principal') && <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student Name</th>}
                            <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                            <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
                            <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Due Date</th>
                            <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                            <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                        {paymentsData.map((payment) => (
                            <tr key={payment.id}>
                                {(currentUser.role === 'admin' || currentUser.role === 'principal') && <td className="px-6 py-4 whitespace-nowrap">{payment.studentName || 'N/A'}</td>}
                                <td className="px-6 py-4 whitespace-nowrap">{payment.type}</td>
                                <td className="px-6 py-4 whitespace-nowrap">{payment.amount.toLocaleString()}</td>
                                <td className="px-6 py-4 whitespace-nowrap">{payment.dueDate}</td>
                                <td className={`px-6 py-4 whitespace-nowrap font-semibold ${
                                    payment.status === 'Paid' ? 'text-green-600' :
                                    payment.status === 'Due' ? 'text-red-600' : 'text-yellow-600'
                                }`}>{payment.status}</td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                    {payment.status === 'Due' && currentUser.role === 'student' && (
                                        <button className="text-blue-600 hover:text-blue-900 mr-2">Pay Now</button>
                                    )}
                                    {payment.status === 'Pending Proof' && currentUser.role === 'admin' && (
                                        <button className="text-purple-600 hover:text-purple-900 mr-2">View Proof</button>
                                    )}
                                    {(currentUser.role === 'admin' || currentUser.role === 'principal') && payment.status !== 'Paid' && (
                                        <button className="text-green-600 hover:text-green-900"
                                            onClick={async () => {
                                                try {
                                                    const markReceivedResponse = await fetch(`http://127.0.0.1:8000/api/payments/mark-received/${payment.id}/`, {
                                                        method: 'PATCH',
                                                        headers: { 'Authorization': `Bearer ${currentUser.token}` }
                                                    });
                                                    if (!markReceivedResponse.ok) throw new Error('Failed to mark payment received');
                                                    fetchPayments();
                                                } catch (err) {
                                                    console.error('Error marking payment received:', err);
                                                    // In production, use a custom modal instead of alert
                                                    alert(`Error: ${err.message}`);
                                                }
                                            }}>Mark Received</button>
                                    )}
                                    {(currentUser.role === 'admin' || currentUser.role === 'principal') && (
                                        <button className="text-gray-600 hover:text-gray-900 ml-2">Details</button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {(currentUser.role === 'admin' || currentUser.role === 'principal') && (
                    <button className="mt-4 bg-purple-600 hover:bg-purple-700 text-white py-2 px-4 rounded-lg shadow-sm">Issue New Payment/Fine</button>
                )}
            </div>
        );
    };

    return (
        <div>
            <SectionTitle>Payments</SectionTitle>

            {currentUser.role === 'student' && (
                <div className="bg-white p-6 rounded-lg shadow-md mb-6">
                    <SectionTitle>Upload Payment Proof</SectionTitle>
                    <form onSubmit={handleUploadSubmit} className="flex flex-col space-y-4">
                        <input
                            type="file"
                            accept=".jpg,.pdf"
                            onChange={handleFileChange}
                            className="block w-full text-sm text-gray-500
                            file:mr-4 file:py-2 file:px-4
                            file:rounded-full file:border-0
                            file:text-sm file:font-semibold
                            file:bg-blue-50 file:text-blue-700
                            hover:file:bg-blue-100"
                        />
                        <button
                            type="submit"
                            disabled={!selectedFile}
                            className="bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded-lg shadow-md transition duration-200 disabled:opacity-50"
                        >
                            Submit Proof
                        </button>
                        {uploadMessage.text && (
                            <p className={`text-sm ${uploadMessage.type === 'success' ? 'text-green-600' : 'text-red-600'}`}>
                                {uploadMessage.text}
                            </p>
                        )}
                    </form>
                </div>
            )}

            {renderPaymentsTable()}
        </div>
    );
};

