            </div>
            {users.length === 0 ? (
                <div className="text-center py-4 text-gray-600">No users found for this filter.</div>
            ) : (
                <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                    <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-50">
                            <tr>
                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Username</th>
                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Role</th>
                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                            </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                            {users.map((user) => (
                                <tr key={user.id}>
                                    <td className="px-6 py-4 whitespace-nowrap">{user.username}</td>
                                    <td className="px-6 py-4 whitespace-nowrap">{user.role}</td>
                                    <td className="px-6 py-4 whitespace-nowrap">{user.email}</td>
                                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                        <button className="text-blue-600 hover:text-blue-900 mr-2">View</button>
                                        <button className="text-indigo-600 hover:text-indigo-900 mr-2">Edit</button>
                                        <button className="text-red-600 hover:text-red-900">Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};


// --- Main App Component ---
const App = () => {
    // currentUser will store user's role and other info upon successful login
    const [currentUser, setCurrentUser] = useState(null); // { id: 'someId', username: 'testuser', role: 'students', token: 'xyz' }
    // currentPage will manage routing within the application
    const [currentPage, setCurrentPage] = useState('login'); // 'login', 'dashboard', 'attendance', 'grades', etc.

    // Determine which component to render based on current state
    const renderContent = () => {
        if (!currentUser) {
            return <LoginPage setCurrentUser={setCurrentUser} setCurrentPage={setCurrentPage} />;
        }

        switch (currentPage) {
            case 'login':
                return <LoginPage setCurrentUser={setCurrentUser} setCurrentPage={setCurrentPage} />;
            case 'dashboard':
                switch (currentUser.role) {
                    case 'students':
                        return <StudentDashboard />;
                    case 'parents':
                        return <ParentDashboard />;
                    case 'teachers':
                        return <TeacherDashboard />;
                    case 'admin':
                    case 'principal':
                        return <AdminDashboard />;
                    default:
                        return <div className="text-center py-10">Unknown role dashboard.</div>;
                }
            case 'attendance':
                return <AttendancePage />;
            case 'grades':
                return <GradesPage />;
            case 'leaves':
                return <LeavesPage />;
            case 'library':
                return <LibraryPage />;
            case 'payments':
                return <PaymentsPage />;
            case 'timetable':
                return <TimetablePage />;
            case 'myinfo':
                return <MyInfoCornerPage />;
            case 'college-logs':
                return <CollegeInOutLogPage />;
            case 'hostel-attendance':
                return <HostelAttendancePage />;
            case 'users-info':
                return <StudentsFacultyInfoPage />;
            default:
                return <div className="text-center py-10">Page Not Found.</div>;
        }
    };

    return (
        // Provide currentUser and functions to update state to all children via context
        <AppContext.Provider value={{ currentUser, setCurrentUser, currentPage, setCurrentPage }}>
            {renderContent()}
        </AppContext.Provider>
    );
};

export default App;
