// src/LoginPage.js
import React, { useState, useContext } from 'react';
import { AppContext } from './App'; // Import AppContext from App.js

const LoginPage = () => {
    // Access global state and functions from context
    const { setCurrentUser, setCurrentPage } = useContext(AppContext);

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('');
    const [loginMessage, setLoginMessage] = useState({ text: '', type: '' });

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoginMessage({ text: '', type: '' });

        if (!username || !password || !role) {
            setLoginMessage({ text: 'Please fill in all fields.', type: 'error' });
            return;
        }

        const backendUrl = `http://127.0.0.1:8000/api/users/login/`; // Correct login endpoint

        // The API specifies /api/users/login/ and role in payload
        const payload = {
            username: username,
            password: password,
            role: role
        };

        setLoginMessage({ text: 'Logging in...', type: 'info' });

        try {
            const response = await fetch(backendUrl, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(payload)
            });

            if (response.ok) {
                const data = await response.json();
                setLoginMessage({ text: 'Login successful! Welcome.', type: 'success' });
                // Assuming backend returns user_id and token
                setCurrentUser({
                    id: data.user_id || 'mockUserId',
                    username: username,
                    role: role,
                    token: data.token // Store the token
                });
                setCurrentPage('dashboard'); // Redirect to dashboard after login
            } else {
                const errorData = await response.json();
                setLoginMessage({ text: `Login failed: ${errorData.message || 'Invalid credentials.'}`, type: 'error' });
                console.error('Login failed:', response.status, errorData);
            }
        } catch (error) {
            setLoginMessage({ text: 'An error occurred during login. Please try again later.', type: 'error' });
            console.error('Fetch error:', error);
        }
    };

    const handleForgotPassword = () => {
        setLoginMessage({ text: 'Forgot password functionality is not yet implemented. Please contact support.', type: 'info' });
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
            <div className="bg-white p-8 rounded-xl shadow-2xl max-w-sm w-full text-center">
                {/* MAMC Logo/Image - Updated path for local project */}
                <img
                    src="/images/mamc-logo.png" // This path assumes you saved the image in public/images/mamc-logo.png
                    alt="Maharaja Agrasen Medical College Logo"
                    className="mamc-logo mx-auto mb-6 rounded-lg w-full h-auto"
                    onError={(e) => {
                        e.currentTarget.onerror = null;
                        e.currentTarget.src = 'https://placehold.co/400x100/A0A0A0/FFFFFF?text=MAMC+Logo'; // Fallback placeholder
                    }}
                />

                <h2 className="text-3xl font-extrabold text-gray-900 mb-8">MAMC Portal</h2>

                <form onSubmit={handleSubmit} className="flex flex-col gap-y-5">
                    <input
                        type="text"
                        id="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg text-lg focus:ring-blue-500 focus:border-blue-500 transition duration-200"
                        required
                    />
                    <input
                        type="password"
                        id="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg text-lg focus:ring-blue-500 focus:border-blue-500 transition duration-200"
                        required
                    />
                    <select
                        id="role"
                        value={role}
                        onChange={(e) => setRole(e.target.value)}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg text-lg bg-white appearance-none cursor-pointer focus:ring-blue-500 focus:border-blue-500 transition duration-200"
                        required
                    >
                        <option value="" disabled>Select Your Role</option>
                        <option value="admin">Admin</option>
                        <option value="student">Student</option>
                        <option value="parent">Parent</option>
                        <option value="teacher">Teacher</option>
                        <option value="principal">Principal</option>
                    </select>

                    <button
                        type="submit"
                        className="w-full py-3 px-6 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg shadow-md transition duration-300 ease-in-out transform hover:-translate-y-1 active:translate-y-0 text-lg"
                    >
                        Login
                    </button>
                </form>

                {/* Forgot Password Link */}
                <button
                    onClick={handleForgotPassword}
                    className="mt-4 text-blue-600 hover:underline text-sm"
                >
                    Forgot Password?
                </button>

                {loginMessage.text && (
                    <div
                        className={`mt-6 p-4 rounded-lg text-left ${
                            loginMessage.type === 'success' ? 'bg-green-100 text-green-800 border border-green-300' :
                            loginMessage.type === 'error'   ? 'bg-red-100 text-red-800 border border-red-300' :
                                                              'bg-blue-100 text-blue-800 border border-blue-300'
                        }`}
                    >
                        {loginMessage.text}
                    </div>
                )}
            </div>
        </div>
    );
};

export default LoginPage;
