const StudentsFacultyInfoPage = () => {
    const { currentUser } = useContext(AppContext);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [filterRole, setFilterRole] = useState('');
    const [editUser, setEditUser] = useState(null);  // for editing user
    const [editFormData, setEditFormData] = useState({});  // editable fields

    const handleEdit = (user) => {
      setEditUser(user);
      setEditFormData({
          username: user.username,
          email: user.email,
          role: user.role,
          full_name: user.full_name || '',
      });
    };

    const handleDelete = async (userId) => {
      if (!window.confirm("Are you sure you want to delete this user? This action is irreversible.")) return;

      try {
          const response = await fetch(`http://127.0.0.1:8000/api/users/${userId}/`, {
              method: 'DELETE',
              headers: { 'Authorization': `Bearer ${currentUser.token}` }
          });

          if (!response.ok) {
              const errorData = await response.json();
              throw new Error(errorData.detail || errorData.message || 'Failed to delete user');
          }

          alert('User deleted successfully.');
          setUsers(users.filter(u => u.id !== userId)); // Remove user from UI
      } catch (err) {
          console.error('Delete User Error:', err);
          alert(`Error deleting user: ${err.message}`);
      }
    };
    React.useEffect(() => {
        const fetchUsers = async () => {
            setLoading(true);
            setError('');
            if (!currentUser || !currentUser.token) {
                setError('User not authenticated for Users Info.');
                setLoading(false);
                console.log('StudentsFacultyInfoPage: currentUser or token missing.', currentUser);
                return;
            }

            if (currentUser.role !== 'admin' && currentUser.role !== 'principal' && currentUser.role !== 'hidden_superuser') {
                setError('Unauthorized access for Users Info. Only Admin/Principal can view.');
                setLoading(false);
                console.log('StudentsFacultyInfoPage: Unauthorized role for fetch.', currentUser.role);
                return;
            }
            console.log('StudentsFacultyInfoPage: Fetching users with token:', currentUser.token);

            try {
                let url = `http://127.0.0.1:8000/api/users/list/`;
                if (filterRole) {
                    url += `?role=${filterRole}`;
