                const response = await fetch(url, {
                    headers: { 'Authorization': `Bearer ${currentUser.token}` }
                });
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.detail || errorData.message || 'Failed to fetch users list');
                }
                const data = await response.json();
                setUsers(data);
                setLoading(false);
                console.log('StudentsFacultyInfoPage: Users fetched successfully.', data);
            } catch (err) {
                setError(`Failed to load user information: ${err.message}`);
                setLoading(false);
                console.error('Fetch error (Users Info):', err);
            }
